import { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

export default function SignUp() {
  const history = useHistory();
  const [regFirstName, setRegFirstName] = useState("");
  const [regLastName, setRegLastName] = useState("");
  const [regEmail, setRegEmail] = useState("");
  const [regPass, setRegPass] = useState("");
  const handleRegisterSubmit = (event) => {
    event.preventDefault();
    const reactRegData = { regFirstName, regLastName, regEmail, regPass };
    const url = `${process.env.REACT_APP_SERVER_URL}/api/signup/`;
    axios
      .post(url, reactRegData)
      .then((res) => {
        console.log("registration details send");
        console.log(res);
      })
      .catch((err) => console.log("--->--", err.data));

    history.push("/login");
  };

  return (
    <>
      <h1>SignUp page</h1>
      <div className="row">
        <form
          onSubmit={handleRegisterSubmit}
          className="contact-form mycontform"
          style={{ width: "40rem" }}
        >
          <div className="form-group myformgrp">
            <input
              type="text"
              placeholder="Please enter your firstname"
              onChange={(e) => setRegFirstName(e.target.value)}
              className="form-control"
            />
          </div>
          <div className="form-group myformgrp">
            <input
              type="text"
              placeholder="Please enter your lastname"
              onChange={(e) => setRegLastName(e.target.value)}
              className="form-control"
            />
          </div>
          <div className="form-group myformgrp">
            <input
              type="text"
              placeholder="Please enter your email"
              onChange={(e) => setRegEmail(e.target.value)}
              className="form-control"
            />
          </div>
          <div className="form-group myformgrp">
            <input
              type="password"
              placeholder="Please enter your password"
              onChange={(e) => setRegPass(e.target.value)}
              className="form-control"
            />
          </div>
          <button
            type="submit"
            value="Sign Up"
            class="btn btn-primary mybtncss"
          >
            Sign Up
          </button>
        </form>
      </div>
    </>
  );
}
